#include "opencv2/highgui/highgui.hpp""#include "opencv2/imgproc/imgproc.hpp"#include <iostream> using namespace cv;using namespace std; int main( ){        Mat image,dst;       image = imread("melanoma_1.jpg", CV_LOAD_IMAGE_COLOR);          // structural element for performing dilation and erosion       int erosion_size = 6;         Mat element = getStructuringElement(cv::MORPH_CROSS,              cv::Size(2 * erosion_size + 1, 2 * erosion_size + 1),              cv::Point(erosion_size, erosion_size) );        // Apply erosion or dilation on the image       erode(image,dst,element);  // dilate(image,dst,element);              namedWindow( "input image window", CV_WINDOW_AUTOSIZE );         imshow( "input image window", image );                         namedWindow( "output  window", CV_WINDOW_AUTOSIZE );          imshow( "output window", dst );        waitKey(0);                                                return 0;}